const typeLookup = {
    trash: {
        text: "Silmek",
        requireArgs: ["id"]
    },
    complete: {
        text: "Tamamlamak",
        requireArgs: ["id"]
    },
    open: {
        text: "Açmak",
        requireArgs: ["id"]
    },
    add: {
        text: "Eklemek",
        requireArgs: ["name"]
    },
    update: {
        text: "Güncellemek",
        requireArgs: ["id", "name"]
    },
    schedule: {
        text: "Hatırlatma Kurmak",
        requireArgs: ["id"]
    }
}

if (!Object.keys(typeLookup).includes(msg.req.params.type)) {
    msg.error = {
        message: `${msg.req.params.type} geçersiz bir parametre değeridir.`
    }
    return [null, msg]
}

if (!typeLookup[msg.req.params.type].requireArgs.every(arg => msg.payload[arg])) {
    msg.error = {
        message: `${typeLookup[msg.req.params.type].text} istediğiniz elemanın ${typeLookup[msg.req.params.type].requireArgs} bilgisi(leri) olmalı.`
    }
    return [null, msg]
}

return msg;

const DB = global.get("DB")

let todo = DB.todo
const date = new Date()
const now = date.getTime()

if (msg.req.params.type === "add") {
    todo.push({
        id: now,
        name: msg.payload.name,
        done: false,
        stamp: {
            createdAt: now,
            ip: msg.req.ip,
            username: msg.user.username || "",
            email: msg.user.email || ""
        },
        date: date.toLocaleDateString("TR"), // DD.AA.YYYY
        time: date.toLocaleTimeString("TR") // SS:DD:NN
    })
} else {
    const index = todo.findIndex(x => x.id == msg.payload.id)

    if (!~index) {
        msg.error = {
            message: "İd ile eşleşen bir kayıt bulunamadı."
        }
        return [null, msg]
    }

    if(todo[index].stamp.username !== msg.user.username && !msg.user.roles.includes("admin")) {
        msg.error = {
            message: "Yetkisiz erişim."
        }
        return [null, msg]
    }

    if (msg.req.params.type === "update" && todo[index].done) {
        msg.error = {
            message: "Tamamlanan işler güncellenemez."
        }
        return [null, msg]
    }

    if (msg.req.params.type === "trash") {
        todo.splice(index, 1)
    } else if (msg.req.params.type === "schedule") {
        // In-Memory Schedule
        let scheduleOptions = {
            "crontiMethod": "onIntervalTime",
            "crontiArgs": [msg.payload.datetime, msg.payload.datetime]
        }
        todo[index].schedule = {
            formData: msg.payload,
            options: scheduleOptions
        }
        msg.payload = scheduleOptions
        msg.todo = todo[index]
    } else {
        switch (msg.req.params.type) {
            case "complete":
                todo[index].done = true
                break;
            case "open":
                todo[index].done = false
                break;
            case "update":
                todo[index].name = msg.payload.name
                break;
        }
        todo[index].stamp = {
            createdAt: now,
            ip: msg.req.ip,
            username: msg.user.username || "",
            email: msg.user.email || ""
        }
        todo[index].date = date.toLocaleDateString("TR") // DD.AA.YYYY
        todo[index].time = date.toLocaleTimeString("TR") // SS:DD:NN
    }
}

DB.todo = todo

global.set("DB", DB)

return msg.req.params.type === "schedule" ? [msg, null, msg] : msg

