const DB = global.get("DB")

msg.payload = DB.user?.map(x => (x.hasAdmin = x.roles.includes("admin"), x)).sort((a, b) => b.stamp.createdAt - a.stamp.createdAt)

return msg

const typeLookup = {
    add: {
        text: "Eklemek",
        requireArgs: ["username", "password", "email"],
        validation: {
            email(data, formData) { return /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(data) && !global.get("DB").user.some(u => u.email === data) }
        }
    },
    update: {
        text: "Güncellemek",
        requireArgs: ["id", "!username", "password", "email", "roles"],
        validation: {
            email(data, formData) { return /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(data) && !global.get("DB").user.some(u => u.id != formData.id && u.email === data) }
        }
    }
}

if (!Object.keys(typeLookup).includes(msg.req.params.type)) {
    msg.error = {
        message: `${msg.req.params.type} geçersiz bir parametre değeridir.`,
        formData: msg.payload
    }
    return msg.req.headers.referer.includes("user-edit") ? [null, null, msg] : [null, msg]
}

let fields = Object.keys(msg.payload)
if (!typeLookup[msg.req.params.type].requireArgs.every(arg => {
    return ((typeLookup[msg.req.params.type].validation && typeLookup[msg.req.params.type].validation[arg]) 
        ? typeLookup[msg.req.params.type].validation[arg](msg.payload[arg], msg.payload) 
        : true) && (arg.startsWith("!") ? !fields.includes(arg.substr(1)) : fields.includes(arg))
    })) {
    msg.error = {
        message: `${typeLookup[msg.req.params.type].text} istediğiniz elemanın ${typeLookup[msg.req.params.type].requireArgs} bilgisi(leri) olma(ma)lı.`,
        formData: msg.payload
    }
    msg.referer = msg.req.headers.referer
    return msg.req.headers.referer.includes("user-edit") ? [null, null, msg] : [null, msg]
}

msg.payload.roles = Array.isArray(msg.payload.roles) ? msg.payload.roles : [msg.payload.roles]
return msg;

const DB = global.get("DB")

let user = DB.user
const date = new Date()
const now = date.getTime()

if (msg.req.params.type === "add") {
    user.push({
        id: now,
        active: true,
        stamp: {
            createdAt: now,
            ip: msg.req.ip,
            username: msg.user.username || "",
            email: msg.user.email || ""
        },
        roles: ["user"],
        date: date.toLocaleDateString("TR"), // DD.AA.YYYY
        time: date.toLocaleTimeString("TR"), // SS:DD:NN
        ...msg.payload
    })
} else {
    const index = user.findIndex(x => x.id == msg.payload.id)

    if (!~index) {
        msg.error = {
            message: "İd ile eşleşen bir kayıt bulunamadı.",
            formData: msg.payload
        }
        return [null, msg]
    }

    if (msg.payload.username) {
        msg.error = {
            message: "Kullanıcı adı güncellenemez.",
            formData: msg.payload
        }
        return [null, msg]
    }

    msg.payload.active = msg.payload.status === "active"
    delete msg.payload.status
    user[index] = { ...user[index], ...msg.payload }
    user[index].stamp = {
        createdAt: now,
        ip: msg.req.ip,
        username: "",
        email: ""
    }
    user[index].date = date.toLocaleDateString("TR") // DD.AA.YYYY
    user[index].time = date.toLocaleTimeString("TR") // SS:DD:NN
}

DB.user = user

global.set("DB", DB)

return msg

